from __future__ import annotations
import sys
from importlib.machinery import ModuleSpec, PathFinder
from importlib.machinery import all_suffixes as module_suffixes
from importlib.util import spec_from_file_location
from itertools import chain
from pathlib import Path

MAPPING: dict[str, str] = {'wakamate_backend': '/root/WakaMate-AI/wakamate_backend', 'wakamate_deliver_route': '/root/WakaMate-AI/wakamate_deliver_route', 'wakamate_frontend': '/root/WakaMate-AI/wakamate_frontend', 'wakamate_inventory_summary': '/root/WakaMate-AI/wakamate_inventory_summary'}
NAMESPACES: dict[str, list[str]] = {'wakamate_deliver_route': ['/root/WakaMate-AI/wakamate_deliver_route'], 'wakamate_inventory_summary': ['/root/WakaMate-AI/wakamate_inventory_summary'], 'wakamate_backend': ['/root/WakaMate-AI/wakamate_backend'], 'wakamate_frontend': ['/root/WakaMate-AI/wakamate_frontend'], 'wakamate_deliver_route.src': ['/root/WakaMate-AI/wakamate_deliver_route/src'], 'wakamate_deliver_route.configs': ['/root/WakaMate-AI/wakamate_deliver_route/configs'], 'wakamate_deliver_route.src.wakamate_deliver_route.configs': ['/root/WakaMate-AI/wakamate_deliver_route/src/wakamate_deliver_route/configs'], 'wakamate_inventory_summary.src': ['/root/WakaMate-AI/wakamate_inventory_summary/src'], 'wakamate_inventory_summary.configs': ['/root/WakaMate-AI/wakamate_inventory_summary/configs'], 'wakamate_inventory_summary.src.wakamate_inventory_summary.configs': ['/root/WakaMate-AI/wakamate_inventory_summary/src/wakamate_inventory_summary/configs'], 'wakamate_backend.utils': ['/root/WakaMate-AI/wakamate_backend/utils'], 'wakamate_backend.controller': ['/root/WakaMate-AI/wakamate_backend/controller'], 'wakamate_backend.config': ['/root/WakaMate-AI/wakamate_backend/config'], 'wakamate_backend.model': ['/root/WakaMate-AI/wakamate_backend/model'], 'wakamate_backend.routes': ['/root/WakaMate-AI/wakamate_backend/routes'], 'wakamate_backend.middleware': ['/root/WakaMate-AI/wakamate_backend/middleware'], 'wakamate_frontend.src': ['/root/WakaMate-AI/wakamate_frontend/src'], 'wakamate_frontend.public': ['/root/WakaMate-AI/wakamate_frontend/public'], 'wakamate_frontend.src.layout': ['/root/WakaMate-AI/wakamate_frontend/src/layout'], 'wakamate_frontend.src.public': ['/root/WakaMate-AI/wakamate_frontend/src/public'], 'wakamate_frontend.src.Pages': ['/root/WakaMate-AI/wakamate_frontend/src/Pages'], 'wakamate_frontend.src.api': ['/root/WakaMate-AI/wakamate_frontend/src/api'], 'wakamate_frontend.src.components': ['/root/WakaMate-AI/wakamate_frontend/src/components'], 'wakamate_frontend.src.routes': ['/root/WakaMate-AI/wakamate_frontend/src/routes'], 'wakamate_frontend.src.assets': ['/root/WakaMate-AI/wakamate_frontend/src/assets'], 'wakamate_frontend.src.components.auth': ['/root/WakaMate-AI/wakamate_frontend/src/components/auth'], 'wakamate_frontend.public.images': ['/root/WakaMate-AI/wakamate_frontend/public/images']}
PATH_PLACEHOLDER = '__editable__.wakamate_ai-0.1.0.finder' + ".__path_hook__"


class _EditableFinder:  # MetaPathFinder
    @classmethod
    def find_spec(cls, fullname: str, path=None, target=None) -> ModuleSpec | None:  # type: ignore
        # Top-level packages and modules (we know these exist in the FS)
        if fullname in MAPPING:
            pkg_path = MAPPING[fullname]
            return cls._find_spec(fullname, Path(pkg_path))

        # Handle immediate children modules (required for namespaces to work)
        # To avoid problems with case sensitivity in the file system we delegate
        # to the importlib.machinery implementation.
        parent, _, child = fullname.rpartition(".")
        if parent and parent in MAPPING:
            return PathFinder.find_spec(fullname, path=[MAPPING[parent]])

        # Other levels of nesting should be handled automatically by importlib
        # using the parent path.
        return None

    @classmethod
    def _find_spec(cls, fullname: str, candidate_path: Path) -> ModuleSpec | None:
        init = candidate_path / "__init__.py"
        candidates = (candidate_path.with_suffix(x) for x in module_suffixes())
        for candidate in chain([init], candidates):
            if candidate.exists():
                return spec_from_file_location(fullname, candidate)
        return None


class _EditableNamespaceFinder:  # PathEntryFinder
    @classmethod
    def _path_hook(cls, path) -> type[_EditableNamespaceFinder]:
        if path == PATH_PLACEHOLDER:
            return cls
        raise ImportError

    @classmethod
    def _paths(cls, fullname: str) -> list[str]:
        paths = NAMESPACES[fullname]
        if not paths and fullname in MAPPING:
            paths = [MAPPING[fullname]]
        # Always add placeholder, for 2 reasons:
        # 1. __path__ cannot be empty for the spec to be considered namespace.
        # 2. In the case of nested namespaces, we need to force
        #    import machinery to query _EditableNamespaceFinder again.
        return [*paths, PATH_PLACEHOLDER]

    @classmethod
    def find_spec(cls, fullname: str, target=None) -> ModuleSpec | None:  # type: ignore
        if fullname in NAMESPACES:
            spec = ModuleSpec(fullname, None, is_package=True)
            spec.submodule_search_locations = cls._paths(fullname)
            return spec
        return None

    @classmethod
    def find_module(cls, _fullname) -> None:
        return None


def install():
    if not any(finder == _EditableFinder for finder in sys.meta_path):
        sys.meta_path.append(_EditableFinder)

    if not NAMESPACES:
        return

    if not any(hook == _EditableNamespaceFinder._path_hook for hook in sys.path_hooks):
        # PathEntryFinder is needed to create NamespaceSpec without private APIS
        sys.path_hooks.append(_EditableNamespaceFinder._path_hook)
    if PATH_PLACEHOLDER not in sys.path:
        sys.path.append(PATH_PLACEHOLDER)  # Used just to trigger the path hook
